INSERT INTO `wp_comments` (`comment_ID`, `comment_post_ID`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_IP`, `comment_date`, `comment_date_gmt`, `comment_content`, `comment_karma`, `comment_approved`, `comment_agent`, `comment_type`, `comment_parent`, `user_id`) VALUES
(1, 1, 'یک نویسندهٔ دیدگاه در وردپرس', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2022-02-15 13:58:51', '2022-02-15 10:28:51', 'سلام، این یک دیدگاه است.\nبرای شروع مدیریت، ویرایش و پاک کردن دیدگاه‌ها، لطفا بخش دیدگاه‌ها در پیشخوان را ببینید.\nتصاویر نویسندگان دیدگاه از <a href=\"https://gravatar.com\">Gravatar</a> گرفته می‌شود.', 0, 'trash', '', 'comment', 0, 0),
(2, 1, 'علی صابری', 'alisaberizadeh.one@gmail.com', '', '::1', '2022-02-22 14:46:56', '2022-02-22 11:16:56', 'سلام ، مقاله مفیدی بود', 0, '1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/98.0.4758.102 Safari/537.36', 'comment', 0, 0),
(3, 224, 'زهرا سمیعی', 'zahrasamiii@gmail.com', '', '::1', '2022-02-26 22:16:15', '2022-02-26 18:46:15', 'همین رو کم داشتیم !!!', 0, '1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/98.0.4758.102 Safari/537.36', 'comment', 0, 0),
(4, 195, 'سعید یاسینی', 'saiidyas@gmail.com', '', '::1', '2022-02-26 22:17:11', '2022-02-26 18:47:11', 'سلام ، پست مفیدی بود ، بنظرم از بوت استرپ بهتره', 0, '1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/98.0.4758.102 Safari/537.36', 'comment', 0, 0),
(5, 239, 'علیرضا وحیدی', 'avahidii@gmail.com', '', '::1', '2022-02-26 22:18:09', '2022-02-26 18:48:09', 'مقاله بسیار خوبی بود ، ممنون از سایتتون', 0, '1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/98.0.4758.102 Safari/537.36', 'comment', 0, 0);
